// src/App.js
import React, { useState } from 'react';
import { uploadVideo } from './api';
import UploadForm from './Uploadform';
import ResultPlayer from './ResultPlayer';
import './index.css';

function App() {
  const [video, setVideo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [summaryUrl, setSummaryUrl] = useState(null);

  const handleFileChange = (e) => {
    setVideo(e.target.files[0]);
    setSummaryUrl(null);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!video) return;

    setLoading(true);
    try {
      const data = await uploadVideo(video);
      const fullUrl = `http://localhost:8000${data.summary_video_url}`;
      setSummaryUrl(fullUrl);
    } catch (err) {
      alert('Error: ' + err.message);
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-wrapper">
      <header className="glass-header">
        <h1 className="logo-text">🎥 Clipify</h1>
        <nav className="nav-links">
          <a href="#upload">Upload</a>
          <a href="#result">Result</a>
          <a href="https://github.com" target="_blank" rel="noopener noreferrer">GitHub</a>
        </nav>
      </header>

      <main className="main-section">
        <section className="intro-section">
          <h2>AI Video Summarization Reinvented</h2>
          <p>Turn lengthy videos into concise summaries with a click. Clipify uses AI to do the hard work for you.</p>
        </section>

        <section className="feature-grid">
          <div className="feature-tile">
            <h3>⚙️ Automation</h3>
            <p>Fully automated AI that selects the best highlights for you.</p>
          </div>
          <div className="feature-tile">
            <h3>🚀 Speed</h3>
            <p>Summarizes your videos in seconds with high accuracy.</p>
          </div>
          <div className="feature-tile">
            <h3>🎯 Simplicity</h3>
            <p>Just upload, click, and watch — no technical skills needed.</p>
          </div>
        </section>

        <section id="upload" className="upload-wrapper">
          <UploadForm
            video={video}
            loading={loading}
            onFileChange={handleFileChange}
            onUpload={handleUpload}
          />
        </section>

        {summaryUrl && (
          <section id="result" className="result-wrapper">
            <ResultPlayer summaryUrl={summaryUrl} />
          </section>
        )}
      </main>

      <footer className="footer-glass">
        <p>© 2025 Clipify | Built with ❤️ using React + FastAPI</p>
      </footer>
    </div>
  );
}

export default App;
