// src/App.js
import React, { useState } from 'react';
import './index.css'; // Keep the modern styling

function App() {
  const [video, setVideo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [summaryUrl, setSummaryUrl] = useState(null);

  const handleFileChange = (e) => {
    setVideo(e.target.files[0]);
    setSummaryUrl(null);
  };

  const handleUpload = async () => {
    if (!video) return;

    const formData = new FormData();
    formData.append("video", video);

    setLoading(true);

    try {
      const response = await fetch("http://localhost:8000/summarize", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error("Upload failed");
      }

      const data = await response.json();
      const fullUrl = `http://localhost:8000${data.summary_video_url}`;
      setSummaryUrl(fullUrl);
    } catch (err) {
      alert("Error: " + err.message);
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      {/* Hero Header */}
      <header className="hero">
        <div className="hero-content">
          <h1>‚ö° AI Video Summarizer</h1>
          <p>Upload your video and get a quick highlight reel ‚Äî powered by deep learning.</p>

          <div className="upload-form">
            <input
              type="file"
              accept="video/*"
              id="video-upload"
              hidden
              onChange={handleFileChange}
            />
            <label htmlFor="video-upload" className="file-label">
              {video ? video.name : "üìÅ Choose Video File"}
            </label>

            <button
              onClick={handleUpload}
              disabled={!video || loading}
              className="submit-btn"
            >
              {loading ? "Summarizing..." : "Summarize Now"}
            </button>
          </div>

          {summaryUrl && (
            <div className="result-section">
              <h4>üé¨ Summarized Video</h4>
              <video src={summaryUrl} controls width="480" />
              <br />
              <a href={summaryUrl} download className="download-btn">
                ‚¨áÔ∏è Download Summary
              </a>
            </div>
          )}
        </div>

        <div className="hero-image">
          <img src="frontend\src\media\aiml.jpg" alt="AI Video Illustration" />
        </div>
      </header>

      <footer className="footer">
        <p>¬© 2025 AI Video Summarizer</p>
        <div className="footer-links">
          <a href="#">Privacy Policy</a>
          <a href="#">Terms</a>
          <a href="#">GitHub</a>
        </div>
      </footer>
    </div>
  );
}

export default App;
