// src/App.js
import React, { useState } from 'react';
import { uploadVideo } from './api';
import UploadForm from './UploadForm';
import ResultPlayer from './ResultPlayer';
import './index.css';

function App() {
  const [video, setVideo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [summaryUrl, setSummaryUrl] = useState(null);

  const handleFileChange = (e) => {
    setVideo(e.target.files[0]);
    setSummaryUrl(null);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!video) return;

    setLoading(true);
    try {
      const data = await uploadVideo(video);
      const fullUrl = `http://localhost:8000${data.summary_video_url}`;
      setSummaryUrl(fullUrl);
    } catch (err) {
      alert('Error: ' + err.message);
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <header className="navbar">
        <h1>üéûÔ∏è Clipify</h1>
        <nav>
          <a href="#">Home</a>
          <a href="#">GitHub</a>
        </nav>
      </header>

      <main className="container">
        <section className="hero">
          <div className="hero-text">
            <h2>Summarize Videos Instantly with AI</h2>
            <p>Upload your video and let Clipify highlight the best parts automatically.</p>
          </div>
          <div className="hero-image">
            <img
              src="https://cdn-icons-png.flaticon.com/512/512/512142.png"
              alt="Video Summary"
            />
          </div>
        </section>

        <UploadForm
          video={video}
          loading={loading}
          onFileChange={handleFileChange}
          onUpload={handleUpload}
        />

        {summaryUrl && <ResultPlayer summaryUrl={summaryUrl} />}
      </main>

      <footer className="footer">
        <p>¬© 2025 Clipify ‚Äî AI-Powered Video Summarization</p>
        <div>
          <a href="#">Privacy</a>
          <a href="#">Terms</a>
        </div>
      </footer>
    </>
  );
}

export default App;
