import React, { useState } from 'react';
import Loader from './loader';

function App() {
  const [video, setVideo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [summaryUrl, setSummaryUrl] = useState(null);

  const handleFileChange = (e) => {
    setVideo(e.target.files[0]);
    setSummaryUrl(null);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!video) return;

    const formData = new FormData();
    formData.append("video", video);
    setLoading(true);

    try {
      const res = await fetch("http://localhost:8000/summarize", {
        method: "POST",
        body: formData
      });

      if (!res.ok) throw new Error("Upload failed");
      const data = await res.json();
      setSummaryUrl(`http://localhost:8000${data.summary_video_url}`);
    } catch (err) {
      alert("Error: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app">
      <h1 className="app-title">VidSage AI</h1>

      <form className="upload-form" onSubmit={handleUpload}>
        <input type="file" id="upload" hidden onChange={handleFileChange} />
        <label htmlFor="upload" className="upload-btn">üìÅ Choose Video</label>
        <button className="submit-btn" disabled={loading}>
          {loading ? "Summarizing..." : "Summarize"}
        </button>
      </form>

      {loading && <Loader />}

      {summaryUrl && (
        <div className="result-section">
          <h2>üé¨ Summary Ready:</h2>
          <video src={summaryUrl} controls width="480" />
          <a href={summaryUrl} download className="download-link">‚¨á Download Summary</a>
        </div>
      )}
    </div>
  );
}

export default App;
