import React, { useState, useEffect } from 'react';

function App() {
  const [video, setVideo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [summaryUrl, setSummaryUrl] = useState(null);
  const [progress, setProgress] = useState(0);
  const [currentTip, setCurrentTip] = useState('');
  const [isDragging, setIsDragging] = useState(false);

  const tips = [
    "Did you know AI can summarize a full lecture in seconds?",
    "Our neural networks are crunching your video...",
    "Let AI do the heavy lifting!",
    "Processing your video with deep learning magic...",
    "Analyzing frames and extracting key moments..."
  ];

  useEffect(() => {
    if (loading) {
      const tipInterval = setInterval(() => {
        setCurrentTip(tips[Math.floor(Math.random() * tips.length)]);
      }, 4000);
      return () => clearInterval(tipInterval);
    }
  }, [loading]);

  const handleFileChange = (e) => {
    setVideo(e.target.files[0]);
    setSummaryUrl(null);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);
    if (e.dataTransfer.files[0]) {
      setVideo(e.dataTransfer.files[0]);
      setSummaryUrl(null);
    }
  };

  const handleUpload = async () => {
    if (!video) return;

    const formData = new FormData();
    formData.append("video", video);

    setLoading(true);
    setProgress(0);

    try {
      const response = await fetch("https://salimshakeel-video-summarization-backend.hf.space/summarize", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error("Upload failed");
      }

      const data = await response.json();
      const fullUrl = `https://salimshakeel-video-summarization-backend.hf.space${data.summary_video_url}`;
      setSummaryUrl(fullUrl);
    } catch (err) {
      alert("Error: " + err.message);
      console.error(err);
    } finally {
      setLoading(false);
      setProgress(0);
    }
  };

  const handleDownload = () => {
    if (!summaryUrl) return;
    const filename = summaryUrl.split('/').pop();
    const downloadUrl = `https://salimshakeel-video-summarization-backend.hf.space/download/${filename}`;
    
    const a = document.createElement('a');
    a.href = downloadUrl;
    a.setAttribute('download', filename);
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0f0c29, #302b63, #24243e)',
      color: 'white',
      position: 'relative',
      overflow: 'hidden',
      padding: '2rem'
    }}>
      {/* Background and animation styles skipped for brevity... */}

      <div style={{
        position: 'relative',
        zIndex: 1,
        maxWidth: '1200px',
        margin: '0 auto',
        textAlign: 'center'
      }}>
        <h1 style={{
          fontSize: '3.5rem',
          fontWeight: '800',
          marginBottom: '2rem',
          background: 'linear-gradient(90deg, #00d4ff, #7e5bef)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          textShadow: '0 0 10px rgba(100, 200, 255, 0.3)'
        }}>
          AI Video Summarization
        </h1>

        {!loading && !summaryUrl && (
          <div
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            onClick={() => document.getElementById('fileInput').click()}
            style={{
              border: `2px dashed ${isDragging ? '#7e5bef' : 'rgba(255, 255, 255, 0.2)'}`,
              borderRadius: '20px',
              padding: '3rem 2rem',
              background: 'rgba(0, 0, 0, 0.2)',
              backdropFilter: 'blur(10px)',
              cursor: 'pointer',
              maxWidth: '600px',
              margin: '0 auto',
            }}
          >
            <input
              id="fileInput"
              type="file"
              accept="video/*"
              onChange={handleFileChange}
              style={{ display: 'none' }}
            />
            <h3>{isDragging ? 'Drop your video here' : 'Upload your video to summarize'}</h3>
            {video && (
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  handleUpload();
                }}
                style={{
                  background: 'linear-gradient(90deg, #00d4ff, #7e5bef)',
                  padding: '0.8rem 2rem',
                  borderRadius: '50px',
                  color: 'white',
                  fontWeight: '600',
                  marginTop: '1rem'
                }}
              >
                Process Video
              </button>
            )}
          </div>
        )}

        {loading && (
          <div>
            <h3>‚è≥ Analyzing your video...</h3>
            <p>{currentTip}</p>
          </div>
        )}

        {summaryUrl && (
          <div style={{ marginTop: '2rem' }}>
            <h3 style={{ color: '#00d4ff' }}>‚úÖ Your summarized video is ready!</h3>
            <video
              src={summaryUrl}
              controls
              style={{
                width: '100%',
                maxWidth: '600px',
                borderRadius: '10px',
                margin: '1rem auto',
              }}
            />
            <div>
              <button
                onClick={handleDownload}
                style={{
                  background: 'linear-gradient(90deg, #00d4ff, #7e5bef)',
                  border: 'none',
                  padding: '0.8rem 2rem',
                  borderRadius: '50px',
                  color: 'white',
                  fontWeight: '600',
                  marginRight: '1rem',
                  cursor: 'pointer'
                }}
              >
                ‚¨áÔ∏è Download Summary
              </button>
              <button
                onClick={() => {
                  setVideo(null);
                  setSummaryUrl(null);
                }}
                style={{
                  border: '1px solid #7e5bef',
                  padding: '0.8rem 2rem',
                  borderRadius: '50px',
                  color: '#fff',
                  fontWeight: '600',
                  background: 'transparent',
                  cursor: 'pointer'
                }}
              >
                üîÅ Summarize Another
              </button>
            </div>
          </div>
        )}
      </div>

      {/* FontAwesome for icons */}
      <link
        rel="stylesheet"
        href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.4/css/all.min.css"
      />
    </div>
  );
}

export default App;
