// src/App.js
import React, { useState } from 'react';
import UploadForm from './Uploadform';
import ResultPlayer from './ResultPlayer';
import './index.css';

function App() {
  const [video, setVideo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [summaryUrl, setSummaryUrl] = useState(null);

  const handleFileChange = (e) => {
    setVideo(e.target.files[0]);
    setSummaryUrl(null);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!video) return;

    const formData = new FormData();
    formData.append('video', video);

    setLoading(true);
    try {
      const response = await fetch('http://localhost:8000/summarize', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      const data = await response.json();
      const fullUrl = `http://localhost:8000${data.summary_video_url}`;
      setSummaryUrl(fullUrl);
    } catch (err) {
      alert('Error: ' + err.message);
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <header>
        <nav>
          <div className="logo">Clipify</div>
        </nav>
      </header>

      <main>
        <section className="hero">
          <div className="hero-content">
            <h1>Clipify: AI Video Summarization</h1>
            <p>
              Upload your video, and let our smart summarizer give you the
              highlights in seconds.
            </p>
          </div>
          <div className="hero-image">
            <img src="/assets/ai.jpg" alt="AI summarizing video" />
          </div>
        </section>

        <UploadForm
          video={video}
          loading={loading}
          onFileChange={handleFileChange}
          onUpload={handleUpload}
        />

        {summaryUrl && <ResultPlayer summaryUrl={summaryUrl} />}
      </main>

      <footer>
        <p>Â© 2025 Clipify - Built with React + FastAPI</p>
        <div className="footer-links">
          <a href="#">Privacy Policy</a>
          <a href="#">Terms</a>
          <a href="#">GitHub</a>
        </div>
      </footer>
    </>
  );
}

export default App;
